import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import {Container, Row, Col} from "react-bootstrap"
import React, { useEffect, useState } from 'react';


export const ManControl = () => {
    return new Promise((resolve) => setTimeout(resolve, 2000));
}

function LoadingButton() {
  const [isLoading, setLoading] = useState(false);

  useEffect(() => {
    if (isLoading) {
      ManControl().then(() => {
        setLoading(false);
      });
    }
  }, [isLoading]);

  const handleClick = () => setLoading(true);


  return (
    <Container>
        <Row>
            <Col>
                <Button variant="primary" type='submit'>
            {isLoading ? 'Loading…' : 'Pour Food for Cat / 200ml'}
                
                    Pour Water for Cat / 200ml
                </Button>
            </Col>
            <Col>
            <Button variant="primary" type='submit'>
            {isLoading ? 'Loading…' : 'Pour Water for Dog / 200g'}
                    Pour Water for Dog / 200ml
                </Button>
                
            </Col>
        </Row>
        <Row>
            <Col>
            <Button variant="primary" type='submit' disabled={isLoading} onClick={!isLoading ? handleClick : null}>
            {isLoading ? 'Loading…' : 'Pour Food for Cat / 200g'}
                    
                </Button>
            </Col>
            <Col>
                <Button variant="primary" type='submit' disabled={isLoading} onClick={!isLoading ? handleClick : null}>
                {isLoading ? 'Loading…' : 'Pour Food for Dog / 200g'}
                </Button>
            </Col>
        </Row>
        <Row>

      <Button href="/" variant="secondary">
        Home
      </Button>

        </Row>
    </Container>
  );
}
